import React, { useState } from "react";

function App() {
  const [demandId, setDemandId] = useState("");
  const [duration, setDuration] = useState(24);
  const [logs, setLogs] = useState([]);
  const [count, setCount] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchLogs = async () => {
    setLoading(true);
    setError(null);
    setLogs([]);
    setCount(null);
    try {
      const res = await fetch(`/api/logs?demandId=${demandId}&duration=${duration}`);
      const data = await res.json();
      if (res.ok) {
        setLogs(data.logs);
        setCount(data.count);
      } else {
        setError(data.error || "Unknown error");
      }
    } catch (err) {
      setError(err.message);
    }
    setLoading(false);
  };

  return (
    <div style={{ padding: 32, fontFamily: 'sans-serif' }}>
      <h2>WFMS Log Search</h2>
      <input
        placeholder="Demand ID"
        value={demandId}
        onChange={e => setDemandId(e.target.value)}
        style={{ marginRight: 8 }}
      />
      <input
        type="number"
        placeholder="Duration (hours)"
        value={duration}
        onChange={e => setDuration(e.target.value)}
        style={{ marginRight: 8, width: 120 }}
        min={1}
      />
      <button onClick={fetchLogs} disabled={loading || !demandId}>
        {loading ? "Loading..." : "Fetch Logs"}
      </button>
      <div style={{ marginTop: 24 }}>
        {error && <div style={{ color: 'red' }}>Error: {error}</div>}
        {count !== null && <div>Log Count: {count}</div>}
        <pre style={{ maxHeight: 400, overflow: "auto", background: "#f5f5f5", padding: 12 }}>
          {logs.map((log, i) => <div key={i}>{log}</div>)}
        </pre>
      </div>
    </div>
  );
}

export default App;
